import React from 'react';
import { useNavigate } from 'react-router-dom';
import TrashIcon from './TrashIcon';
import EditIcon from './EditIcon';

function ProfilePage({ user, setuser, setParentState }) {
  const navigate = useNavigate(); // Hook to navigate programmatically

  function handleLogout() {

    setuser({})
    setParentState(false)
    navigate("/logout")


  }


  // Function to handle the Delete Account button click
  const handleDeleteAccount = async () => {
    try {
      const response = await fetch('http://localhost:5000/deleteAccount', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      const data = await response.json();

      if (response.ok) {
        console.log(data.message);
        setuser({}); // Clear user data in your React component
        setParentState(false);
        navigate('/logout');
      } else {
        console.error('Delete Account failed:', data.error || 'Internal server error');
      }
    } catch (error) {
      console.error('Error during Delete Account:', error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center">
      <div className="max-w-md w-full bg-white p-8 rounded-lg shadow-md">
        <h2 className="text-2xl font-medium text-gray-800 mb-4">Profile</h2>
        {/* <div className="flex items-center justify-center mb-6">
          <img src="https://via.placeholder.com/150" alt="Profile" className="w-20 h-20 rounded-full" />
        </div> */}
        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2">Username:</label>
          <p className="text-gray-800">{user.name}</p>
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2">Email:</label>
          <p className="text-gray-800">{user.email}</p>
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2">Phone:</label>
          <p className="text-gray-800">{user.phone}</p>
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2">Profession:</label>
          <p className="text-gray-800">{user.profession}</p>
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2">Password:</label>
          {/* Avoid rendering password in plain text */}
          <p className="text-gray-800">******</p>
        </div>


        <div className="flex mt-2 justify-between">
        <button onClick={() => handleLogout()} className="bg-blue-500 w-full hover:bg-blue-600 text-white w-[47%] font-medium py-2 px-4 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500">Logout</button>

          <button onClick={handleDeleteAccount}className="bg-red-500 text-left w-[48%] items-center text-center hover:bg-red-600 text-white font-medium py-2 px-4 rounded-md focus:outline-none focus:ring-2 focus:ring-red-500">
            <span className="flex items-center">
              <span className="flex items-center">
                <TrashIcon className="inline" />
                <span className="ml-2">Delete Account</span>
              </span>
            </span>
          </button>
        </div>



      </div>
    </div>
  );
}

export default ProfilePage;
